SnippetCode.val:
using System;
using Chilkat;
public static class Snippet
{
 public static System.String ftpConnection(System.String chilkatAuthenticationCode, System.String proxyHostname, System.String proxyMethod, System.String proxyUsername, System.String proxyPassword, System.String ftpHostname, System.Int32 ftpPortNumber, System.String ftpUsername, System.String ftpPassword, System.String getPut, System.String remoteFileGet, System.String localFileGet, System.String accessGet, System.String createDispGet, System.String remoteFilePut, System.String localFilePut, System.String accessPut, System.String createDispPut, System.Int32 maxRetry, System.String logFile, System.Boolean verboseLogActive)
 {
      //Create a new instance of the Chilkat Ftp2 component.
  SFtp sftp = new SFtp();

  //Debug info.
  //Set to true for debugging.
  sftp.VerboseLogging = verboseLogActive;

  //Variables.
  bool success;
  string handle;

  //Unlocks Chilkat.
  //Saves the last error and returns the lastErrorText on a failure.
  success = sftp.UnlockComponent(chilkatAuthenticationCode);
  //Error check.
  if (success != true) {
   if (logFile != "") {
    sftp.SaveLastError(logFile);
   }
   return "2" + sftp.LastErrorText;
  }

  //Set hostname, method, username and password that are needed for the proxy server connection.
  sftp.HttpProxyDomain = proxyHostname;
  sftp.HttpProxyAuthMethod = proxyMethod;
  sftp.HttpProxyUsername = proxyUsername;
  sftp.HttpProxyPassword = proxyPassword;

  //Connect and login to the SFTP server.
  //Saves the last error and returns the lastErrorText on a failure.
  success = sftp.Connect(ftpHostname, ftpPortNumber);
  //Error check.
  if (success != true) {
   if (logFile != "") {
    sftp.SaveLastError(logFile);
   }
   return "3" + sftp.LastErrorText;
  }

  //Authenticates the provided username en password.
  //Saves the last error and returns the lastErrorText on a failure.
  success = sftp.AuthenticatePw(ftpUsername, ftpPassword);
  //Error check.
  if (success != true) {
   if (logFile != "") {
    sftp.SaveLastError(logFile);
   }
   return "3" + sftp.LastErrorText;
  }

  //Initializes the SFTP subsustem.
  //Saves the last error and returns the lastErrorText on a failure.
  success = sftp.InitializeSftp();
  //Error check.
  if (success != true) {
   if (logFile != "") {
    sftp.SaveLastError(logFile);
   }
   return "3" + sftp.LastErrorText;
  }

  //Uploads or downloads the file to or from the FTP server.
  //Saves the last error and returns the lastErrorText on a failure.
  if(getPut == "g") {
   handle = sftp.OpenFile (remoteFileGet, accessGet, createDispGet);
   if (handle == null) {
    for (int i = 1; i <= maxRetry; i++) {
     System.Threading.Thread.Sleep(5000);
     handle = sftp.OpenFile(remoteFileGet, accessGet, createDispGet);
    }
    //Error check.
    if (handle == null) {
     if (logFile != "") {
      sftp.SaveLastError(logFile);
     }
     return "4" + sftp.LastErrorText;
    }
   }

   //Downloads the file from the server.
   //Saves the last error and returns the lastErrorText on a failure.
   success = sftp.DownloadFile (handle, localFileGet);
   if (success != true) {
    for (int i = 1; i <= maxRetry; i++) {
     System.Threading.Thread.Sleep(5000);
     success = sftp.DownloadFile (handle, localFileGet);
    }
    //Error check.
    if (success != true) {
     if (logFile != "") {
      sftp.SaveLastError(logFile);
     }
     return "4" + sftp.LastErrorText;
    }

    //Close the Handle
    //Saves the last error and returns the lastErrorText on a failure.
    success = sftp.CloseHandle(handle);
    //Error check.
    if (success != true) {
     if (logFile != "") {
     sftp.SaveLastError(logFile);
     }
     return "4" + sftp.LastErrorText;
    }
   }
  } else if(getPut == "p") {
   //Creates or opens a file on the server and fills the handle that is needed to upload a file to the server.
   //Saves the last error and returns the lastErrorText on a failure.
   handle = sftp.OpenFile(remoteFilePut, accessPut, createDispPut); 
   if (handle == null) {
    for (int i = 1; i <= maxRetry; i++) {
     System.Threading.Thread.Sleep(5000);
     handle = sftp.OpenFile(remoteFilePut, accessPut, createDispPut);
    }
    //Error check.
    if (handle == null) {
     if (logFile != "") {
      sftp.SaveLastError(logFile);
     }
     return "5" + sftp.LastErrorText;
    }
   }
   //Uploads file to the server.
   //Saves the last error and returns the lastErrorText on a failure.
   success = sftp.UploadFile(handle, localFilePut);
   if (success != true) {
       for (int i = 1; i <= maxRetry; i++) {
     System.Threading.Thread.Sleep(5000);
     success = sftp.UploadFile (handle, localFilePut);
    }
    if (success != true) {
     if (logFile != "") {
       sftp.SaveLastError(logFile);
     }
        return "5" + sftp.LastErrorText;
    }
   }
   //Close the Handle
   //Saves the last error and returns the lastErrorText on a failure.
   success = sftp.CloseHandle(handle);
   //Error check.
   if (success != true) {
    if (logFile != "") {
     sftp.SaveLastError(logFile);
    }
    return "5" + sftp.LastErrorText;
   }
  }
  //Stops the connection to the SFTP server.
  //Returns "1" 
  sftp.Disconnect();
  return "1";
 }

}
