SQL_STMT_U.val:
select sum(p.ip_provisie),p.ip_basisbedrag_munt from :1 p, :2 i where p.ip_intermediar = ~3 and i.ime_relatienr = ~3 and
p.ip_provisie <>0 and (~4 = ' ' or ~4<>' ' and p.ip_basisbedrag_munt = ~4) and (~5 = 0 or ~5 = 1 and p.ip_referentiedatum between ~6 and ~7) and (~8 = 1 and p.ip_afhandelingsdatum <> '00000000' or ~8 = 0 and p.ip_afhandelingsdatum = '00000000') and /*voorschotten in totaaltelling*/ (~9 = 1 or ~9 = 0 and /*arrangements provisie alleen meenemen als deze niet aan de keten wordt uitgekeerd*/ (p.ip_provisie_soort <> 5 or p.ip_provisie_soort = 5 and not exists ( select 1 from :2 k1 where k1.ime_relatienr = i.ime_ketennr and k1.ime_keten = 1 and k1.ime_uitkeringswijze = 'G')) and
/*Moet de provisiesoort wel worden verwerkt?*/ exists (select 1 from :15 t where t.tb_soort = 'IMPR' and t.tb_nummer = p.ip_provisie_soort and t.tb_symbool = ' ' and t.tb_waarde = 1)and
/*Geen vergunning dan niet bij het saldo optellen*/ /*Vergunning Beleggen nodig voor Instapprovisie en Doorlopende Provisie*/ (~10 = 0 or ~10 = 1 and (((p.ip_provisie_soort = 1 or p.ip_provisie_soort = 2) and i.ime_vergunning_beleggen = 1)or (p.ip_provisie_soort <> 1 and p.ip_provisie_soort <> 2)) and /*Vergunning Sparen nodig voor Rente Share provisie*/ ((p.ip_provisie_soort = 3 and i.ime_vergunning_sparen = 1)or p.ip_provisie_soort <> 3)) and
/*Voorschotten corrigeren */ (~11 = 0 and p.ip_provisie_soort<>10 or ~11 = 1 and (p.ip_provisie_soort <> 10 or p.ip_provisie_soort =10 and ~12 = 1 and p.ip_voorschot_uitgekeerd <> '00000000' or ~13 = 1 and p.ip_voorschot_uitgekeerd = '00000000')) ) and
/*Alleen voorschotten*/ (~9 = 0 or ~9 = 1 and p.ip_provisie_soort = 10 and (~14 = 1 and p.ip_voorschot_uitgekeerd <> '00000000' or ~14 = 0 and p.ip_voorschot_uitgekeerd = '00000000' ))
group by p.ip_basisbedrag_munt
