using System;
using System.Drawing;
using System.Windows.Forms;
using System.Reflection;
using System.Diagnostics;
using System.IO;
using System.Runtime.ConstrainedExecution;
using System.Runtime.InteropServices;
using System.Security;

public static class Snippet
{
 public static System.Double AboutMagicXPA(ref System.Windows.Forms.ListView listComponents)
 {
	try
	{
	// set labels
	FileVersionInfo ff = System.Diagnostics.FileVersionInfo.GetVersionInfo(AppDomain.CurrentDomain.BaseDirectory+"mgcore.dll");
	ListViewItem item;
	System.Collections.Generic.List<String> test = new System.Collections.Generic.List<String>();

	//Version = "Version: " + Application.ProductVersion.ToString();
	//Description =Application.ProductName.ToString();
	//Companyname = Application.CompanyName.ToString();

	//Version = ff.FileVersion;
	//Description = Application.ProductName;
	//Companyname = ff.CompanyName;
	//Copyright = ff.LegalCopyright;


	// display components used by this assembly
	System.Windows.Forms.ColumnHeader ColumnHeader1 = new System.Windows.Forms.ColumnHeader();
	System.Windows.Forms.ColumnHeader ColumnHeader2 = new System.Windows.Forms.ColumnHeader();
	System.Windows.Forms.ColumnHeader ColumnHeader3 = new System.Windows.Forms.ColumnHeader();
	ColumnHeader1.Text = "Name";
	ColumnHeader1.Width = 190;
	ColumnHeader2.Text = "Version";
	ColumnHeader2.Width = 250;
	ColumnHeader2.AutoResize(System.Windows.Forms.ColumnHeaderAutoResizeStyle.HeaderSize);
	ColumnHeader3.Text = "";
	ColumnHeader3.Width = 226;
	ColumnHeader3.AutoResize(System.Windows.Forms.ColumnHeaderAutoResizeStyle.HeaderSize);

	listComponents.AccessibleDescription = "Components List";
	listComponents.AccessibleName = "Components List";
	listComponents.Columns.AddRange(new System.Windows.Forms.ColumnHeader[] {
							ColumnHeader1,
							ColumnHeader2,
							ColumnHeader3});
	listComponents.FullRowSelect = true;
	listComponents.GridLines = true;
	//listComponents.Location = new System.Drawing.Point(8, 96);
	listComponents.Name = "listComponents";
	//listComponents.Size = new System.Drawing.Size(368, 104);

	//listComponents.BackColor=System.Drawing.Color.Azure;
	listComponents.View = System.Windows.Forms.View.Details;

	// Magic
	item = listComponents.Items.Add(Application.ProductName);
	item.SubItems.Add( ff.FileVersion);
	item.SubItems.Add(ff.CompanyName);

	// Windows
	item = listComponents.Items.Add("OS Versie");
	item.SubItems.Add(Environment.OSVersion.VersionString);
	if(Is64BitOperatingSystem())
		item.SubItems.Add("64 Bit");
	else
		item.SubItems.Add("32 Bit");

	// .net
	item = listComponents.Items.Add("Microsoft .Net");
	item.SubItems.Add(Environment.Version.ToString());



	String folder = AppDomain.CurrentDomain.BaseDirectory.ToLower() +"gateways\\" ;

	Process myProcess = Process.GetCurrentProcess();
	if(myProcess != null)
	{
		foreach (ProcessModule processModule in myProcess.Modules)
		{
		if(processModule.FileName.ToLower().StartsWith(folder))
		{
		item =listComponents.Items.Add(processModule.ModuleName);
		item.SubItems.Add(ff.FileVersion);

		test.Add(processModule.ModuleName.Substring(2,processModule.ModuleName.Length-6));
		}
		}
		foreach(String s in test)
		{
		String tempVersie ="";
		String CompanyName = "";
		FileVersionInfo fvi; 

		int a=0;
		int b=0;
		int c=0;
		int d=0;

		foreach (ProcessModule processModule in myProcess.Modules)
		{
		fvi = processModule.FileVersionInfo;
		if(fvi.FileDescription != null && fvi.FileDescription.ToUpper().Contains(s))
		{
		if( CompanyName == "") 
			CompanyName = fvi.CompanyName;

		if(a < fvi.FileMajorPart)
		{
			a = fvi.FileMajorPart;
			b = fvi.FileMinorPart;
			c = fvi.FileBuildPart;
			d = fvi.FilePrivatePart;
			tempVersie = fvi.FileVersion;
		}
		else if(a == fvi.FileMajorPart && b<fvi.FileMinorPart)
		{
			b = fvi.FileMinorPart;
			c = fvi.FileBuildPart;
			d = fvi.FilePrivatePart;
			tempVersie = fvi.FileVersion;
		}
		else if(a == fvi.FileMajorPart && b==fvi.FileMinorPart && c<fvi.FileBuildPart)
		{
			c = fvi.FileBuildPart;
			d = fvi.FilePrivatePart;
			tempVersie = fvi.FileVersion;
		}
		else if(a == fvi.FileMajorPart && b==fvi.FileMinorPart && c==fvi.FileBuildPart && d<fvi.FilePrivatePart)
		{
			d = fvi.FilePrivatePart;
			tempVersie = fvi.FileVersion;
		}


		//item =listComponents.Items.Add(processModule.ModuleName);
		//item.SubItems.Add(processModule.FileVersionInfo.FileVersion);
		//item.SubItems.Add(""+fvi.FileMajorPart+"."+fvi.FileMinorPart+"."+fvi.FileBuildPart+"."+fvi.FilePrivatePart );
		//item.SubItems.Add(processModule.FileVersionInfo.CompanyName);
		}
		}
		if(tempVersie != "")
		{
		item =listComponents.Items.Add(s);
		item.SubItems.Add(tempVersie);
		item.SubItems.Add(CompanyName);
		}
		}
	}



	Assembly[] Assemblys = AppDomain.CurrentDomain.GetAssemblies();
	foreach( Assembly asm in Assemblys )
	{
		string Title ="";
		string Version2 = "";
		string Company = "";


		object[] attributes = asm.GetCustomAttributes(typeof(AssemblyTitleAttribute),false);
		if(attributes.Length >0)
		{
		AssemblyTitleAttribute ata =(AssemblyTitleAttribute) attributes[0];
		Title = ata.Title;
		}
		attributes = asm.GetCustomAttributes(typeof(AssemblyFileVersionAttribute),false);
		if(attributes.Length >0)
		{
		AssemblyFileVersionAttribute ava =(AssemblyFileVersionAttribute) attributes[0];
		Version2 = ava.Version;
		}
		attributes = asm.GetCustomAttributes(typeof(AssemblyCompanyAttribute),false);
		if(attributes.Length >0)
		{
		AssemblyCompanyAttribute aca =(AssemblyCompanyAttribute) attributes[0];
		Company = aca.Company;
		}

		if(Title != "" && Company.Contains("Microsoft")==false && Company.ToLower().Contains("magic")==false && Company != ""  )
		{
		item = listComponents.Items.Add(Title);
		if(Version2=="")
		item.SubItems.Add(FileVersionInfo.GetVersionInfo(asm.Location).FileVersion);
		else
		item.SubItems.Add(Version2);
		item.SubItems.Add(Company);
		}
	}



	}
	catch (Exception e)
	{
		MessageBox.Show("Er is een fout opgetreden:"
		+e.Data.ToString()
		+e.StackTrace.ToString()
		//+e.InnerException.ToString()
		+e.Message.ToString(), "Error occured", MessageBoxButtons.OK, MessageBoxIcon.Error);
		}
		return 0;
	}

	[DllImport("kernel32.dll", CharSet = CharSet.Ansi, SetLastError = true, ExactSpelling = true)]
    private static extern IntPtr GetProcAddress(IntPtr hModule, string methodName);

    [ReliabilityContract(Consistency.WillNotCorruptState, Cer.MayFail), DllImport("kernel32.dll", CharSet = CharSet.Auto, SetLastError = true)]
    private static extern IntPtr GetModuleHandle(string moduleName);

    [DllImport("kernel32.dll", CharSet = CharSet.Auto, SetLastError = true)]
    internal static extern IntPtr GetCurrentProcess();

    [SecurityCritical]
    internal static bool DoesWin32MethodExist(string moduleName, string methodName)
    {
        IntPtr moduleHandle = GetModuleHandle(moduleName);
        if (moduleHandle == IntPtr.Zero)
        {
            return false;
        }
        return (GetProcAddress(moduleHandle, methodName) != IntPtr.Zero);
    }

    [return: MarshalAs(UnmanagedType.Bool)]
    [DllImport("kernel32.dll", SetLastError = true)]
    internal static extern bool IsWow64Process([In] IntPtr hSourceProcessHandle, [MarshalAs(UnmanagedType.Bool)] out bool isWow64);

    [SecuritySafeCritical]
    public static bool Is64BitOperatingSystem()
    {
        bool flag;
        return (IntPtr.Size == 8) ||
            ((DoesWin32MethodExist("kernel32.dll", "IsWow64Process") &&
            IsWow64Process(GetCurrentProcess(), out flag)) && flag);
    }
}

using System;
using System.Diagnostics;
using System.IO;
using System.Windows.Forms;

public static class Snippet
{
 public static System.Double Export(System.String Folder)
 {
  try
  {
   StreamWriter file = new System.IO.StreamWriter( Folder );

   if(IntPtr.Size == 8)
    file.WriteLine("OS Versie;" + Environment.OSVersion.VersionString +
       ";64 bit" );
   else
    file.WriteLine("OS Versie;" + Environment.OSVersion.VersionString +
       ";32 bit" );


   Process myProcess = Process.GetCurrentProcess();
   if(myProcess != null)
   {
    foreach (ProcessModule processModule in myProcess.Modules)
    {
     file.WriteLine(processModule.ModuleName + ";" + processModule.FileVersionInfo.FileVersion +
       ";" + processModule.FileVersionInfo.FileDescription);
    }
   }
   file.Close();
  }
  catch (Exception)
  {
   return 1;
   //MessageBox.Show("Er is een fout opgetreden:"
   //+e.Data.ToString()
   //+e.StackTrace.ToString()
   //+e.InnerException.ToString()
   //+e.Message.ToString(), "Error occured", MessageBoxButtons.OK, MessageBoxIcon.Error);
  }
  return 0;
 }

}
